var vows = require('vows'),
	assert = require('assert');

var ISO8601 = require('../lib/iso8601');
var ISO8601Test = vows.describe('ISO8601 Date String')

ISO8601Test
.addBatch({
	'when given a DateString': {
		topic: new ISO8601('R5/2008-03-01T13:00:00Z/P1Y2M10DT2H30M'),

		'with a valid format': function(iso8601) {
			assert.isNotNull(iso8601);
		},

		'with an inalid format': function(iso8601) {
			assert.isNotNull(iso8601);
		},

		'should be parseable by the Date object': function(iso8601) {
			assert.isNotNull(iso8601);
		}
	}
})
.addBatch({
	'when asked to get the Date': {
		topic: new ISO8601('R5/2008-03-01T13:00:00Z/P1Y2M10DT2H30M'),

		'should get a Date object': function(iso8601) {
			assert.isNotNull(iso8601);
		}
	}
})
.addBatch({
	'when asked to get the Repeat and Duration Information': {
		topic: new ISO8601('R5/2008-03-01T13:00:00Z/P1Y2M10DT2H30M'),

		'should have a repeat value': function(iso8601) {
			assert.isNotNull(iso8601);
		},

		'should have a duration value': function(iso8601) {
			assert.isNotNull(iso8601);
		},

		'should return a single duration': function(iso8601) {
			assert.isNotNull(iso8601);
		},

		'should return durations according to repeat value': function(iso8601) {
			assert.isNotNull(iso8601);
		}
	}
}).export(module);